import './App.css';
import Navbar from './layouts/Navbar'
import {Context} from './Context'
import axios from 'axios'
import { useEffect, useState } from 'react';
import Register from './layouts/Register'
import Home from './Home'
axios.defaults.withCredentials = true
function App() {

  const [verify,setverify] = useState()
  const [username,setusername] = useState("")


  const [clicktoggle,setclicktoggle] = useState(false)




  useEffect(() => {
  axios.get("http://localhost:4100/register/loggedIn").then(res => {
    setverify(res.data)
  }).catch(err => {
    console.log(err)
  })
  },[])


  useEffect(() => {
    axios.get("http://localhost:4100/register/user").then(res => {
      setusername(res.data)
    }).catch(err => {
      console.log(err)
    })

    },[])

    const logout = () => {
      axios.get("http://localhost:4100/register/logout").then(res => {
        console.log("cikis yapildi!!!")
      }).catch(err => {
        console.log(err)
      })
    }






  return (
    <div className="App">

      <Context.Provider value = {{username,logout,clicktoggle,setclicktoggle}}>

        {
          verify ? (
            <div>
            <Navbar/>
            <Home/>
            </div>
          ) : (
            <div>
              <Register/>
            </div>
          )
        }







      </Context.Provider>









    </div>
  );
}

export default App;
