import React from "react";
import styled from "styled-components";
import { useState, useEffect, useContext } from "react";
import { Context } from "../Context";
import { TiGroupOutline } from "react-icons/ti";
import fire from "../firebase";
function Last() {
  const { user } = useContext(Context);
  const { myrooms } = useContext(Context);
  const [joined, setjoined] = useState([]);

  useEffect(() => {
    fire
      .firestore()
      .collectionGroup("wants")
      .where("status", "==", false)
      .where("user", "==", user.displayName)
      .onSnapshot((snapshot) =>
        setjoined(snapshot.docs.map((doc) => doc.data())),
      );
  }, []);

  return (
    <div>
      <h3 style={{ color: "white", padding: "20px" }}>My rooms : </h3>

      {myrooms.map((e) => {
        return (
          <div>
            <Messagediv>
              <TiGroupOutline
                style={{
                  color: "white",
                  fontSize: "40px",
                  textAlign: "center",
                  marginTop: "7px",
                  marginLeft: "10px",
                }}
              />
              <span>
                <span
                  style={{
                    marginLeft: "20px",
                    color: "white",
                    marginBottom: "10px",
                  }}>
                  {e.roomname}
                </span>
                <span
                  style={{
                    float: "right",
                    marginRight: "40px",
                    color: "white",
                  }}>
                  {e.inroom.length}
                </span>
              </span>

              <p>
                <span
                  style={{
                    marginLeft: "70px",
                    color: "white",
                    marginBottom: "10px",
                  }}>
                  {e.user}
                </span>
              </p>
            </Messagediv>

            <h3 style={{ color: "white", padding: "20px", paddingTop: "30px" }}>
              Joined :
            </h3>

            <Joineddiv>sa</Joineddiv>
          </div>
        );
      })}
    </div>
  );
}

export default Last;

const Messagediv = styled.div`
  width: 100%;
  height: 80px;
  border: solid 1px green;
`;

const Joineddiv = styled.div(Messagediv);
