import React, { useState } from 'react'
import axios from 'axios'
import styled from 'styled-components'
import {FaWhatsapp} from 'react-icons/fa'



const Logo = styled.div`
width : 100%;
height : auto;
text-align : center;
padding-top : 20px
`

const Alldiv = styled.div`
width : 30%;
height : 400px;
margin : 20px auto;

@media (max-width: 768px) {
 width : 100%;
height : 600px;
margin-top : 60px

}
`
function Register() {
   const [open,setopen] = useState(false)
   const [email,setemail] = useState("")
   const [username,setusername] = useState("")
   const [password,setpassword] = useState("")
   const [passwordverify,setpasswordverify] = useState("")
   const [errlogin,seterrorlogin] = useState()
   const [errsign,seterrorsign] = useState([])




          const Sigin = () => {
            const user = {
              username,
              email,
              password,
              passwordverify
            }
            axios.post("http://localhost:4100/register/sigin",user).then(() => {
              console.log("kullanici kayit olundu")
              window.location.reload()
            }).catch(err => {
              seterrorsign(err.response.data)
            })
          }



        const Login = () => {
        const user = {
          email,
          password,
        }
        axios.post("http://localhost:4100/register/login",user).then(() => {
          console.log("kullanici kayit olundu")
          window.location.reload()
        }).catch(err => {
          seterrorlogin(err.response.data)
          errlogin.map(e => {
            console.log(e)
          })
        })
        }


  return (
    <div>
    <Logo>
    <FaWhatsapp style = {{fontSize : "80px",color : "white"}}/>
    </Logo>

    <Alldiv>

    {
      open ? (
        <div>
        <h1 style = {{color : "white",textAlign  :"center",paddingTop : "10px",color : "green"}}>Login in</h1>
        <br/>
        <input type = "text" placeholder = "email" className = "form-control" onChange = {(e) => {setemail(e.target.value)}}/>
        <br/>
        <input type = "password" placeholder = "password" className = "form-control" onChange = {(e) => {setpassword(e.target.value)}}/>
        <br/>


        <button className = "btn btn-success" style = {{float : "right",marginRight : "30px"}} onClick = {Login}>Login in</button>
        <p style = {{color : "white",fontWeight : "700"}}>You dont have an account? <span style = {{color: "green",cursor : "pointer"}} onClick= {() => {setopen(!open)}}>Sign in</span></p>   
        <p className = "alert alert-danger">{errlogin[0]}</p>


        </div>
      ) :  (
        <div>
        <h1 style = {{color : "white",textAlign  :"center",paddingTop : "10px",color : "green"}}>Sign in</h1>
        <br/>
        <input type = "text" placeholder = "username" className = "form-control" onChange = {(e) => {setusername(e.target.value)}}/>
        <br/>
        <input type = "text" placeholder = "email" className = "form-control" onChange = {(e) => {setemail(e.target.value)}}/>
        <br/>
        <input type = "password" placeholder = "password" className = "form-control" onChange = {(e) => {setpassword(e.target.value)}}/>
        <br/>
        <input type = "password" placeholder = "password-repeat" className = "form-control" onChange = {(e) => {setpasswordverify(e.target.value)}}/>
        <br/>


        <button className = "btn btn-success" style = {{float : "right",marginRight : "30px"}} onClick = {Sigin}>Sign in</button>
        <p style = {{color : "white",fontWeight : "700"}}>You have an account? <span style = {{color: "green",cursor : "pointer"}} onClick= {() => {setopen(!open)}}>Login</span></p>
        <p className = "alert alert-danger">{errsign}</p>









        </div>
      )
    }

    </Alldiv>







    </div>
  )
}

export default Register
