import React from "react";
import styled from "styled-components";
import { useState, useEffect, useContext } from "react";
import { Context } from "../Context";
import { TiGroupOutline } from "react-icons/ti";
import { BiMessageDetail } from "react-icons/bi";

import fire from "../firebase";
function Last() {
  const { user } = useContext(Context);
  const { myrooms } = useContext(Context);
  const [joined, setjoined] = useState([]);

  useEffect(() => {
    fire
      .firestore()
      .collectionGroup("wants")
      .where("status", "==", true)
      .where("user", "==", user.displayName)
      .onSnapshot((snapshot) =>
        setjoined(snapshot.docs.map((doc) => doc.data())),
      );
  }, []);

  return (
    <div>
      <h4 style={{ color: "white", padding: "20px" }}>My rooms : </h4>

      {myrooms.map((e) => {
        return (
          <div>
            <Messagediv>
              <TiGroupOutline
                style={{
                  color: "white",
                  fontSize: "40px",
                  textAlign: "center",
                  marginTop: "7px",
                  marginLeft: "10px",
                }}
              />

              <span
                style={{
                  marginLeft: "20px",
                  color: "white",
                  marginBottom: "10px",
                }}>
                {e.roomname}
              </span>

              <p>
                <span
                  style={{
                    marginLeft: "70px",
                    color: "white",
                    marginBottom: "10px",
                  }}>
                  {e.user}
                </span>
              </p>

              <span>
                <BiMessageDetail />
              </span>
            </Messagediv>
          </div>
        );
      })}

      {/* joined rooms>Q>>>>>> */}

      <h4 style={{ color: "white", padding: "20px", paddingTop: "30px" }}>
        Joined :
      </h4>

      <Joineddiv>
        {joined.map((e) => {
          return (
            <div>
              <TiGroupOutline
                style={{
                  color: "white",
                  fontSize: "40px",
                  textAlign: "center",
                  marginTop: "7px",
                  marginLeft: "10px",
                }}
              />

              <span
                style={{
                  marginLeft: "20px",
                  color: "white",
                  marginBottom: "10px",
                }}>
                {e.roomname}
              </span>

              <p>
                <span
                  style={{
                    marginLeft: "70px",
                    color: "white",
                    marginBottom: "10px",
                  }}>
                  {e.created}
                </span>
              </p>
            </div>
          );
        })}
      </Joineddiv>
    </div>
  );
}

export default Last;

const Messagediv = styled.div`
  width: 100%;
  height: 80px;
  border: solid 1px #60ff83;
  margin-top: 10px;
`;

const Joineddiv = styled(Messagediv)``;
